def affichage():
	#print("affichage")
	gridcolumn = 0
	gridrow = 0



	for row in grid:
		for col in row:
			color = WHITE
			if col == 0:
				color = (0,0,0)
			if col == 1:
				color = (0,255,0)
			if col == 2:
				color = (255,0,0)
			if col == 3:
				color = (0,0,255)
			if col == 4:
				color = (255,255,0)
			pygame.draw.rect(win, (color), (WIDTH * gridcolumn, HEIGHT * gridrow, WIDTH, HEIGHT))
			#pygame.draw.rect(win,(BLACK), (WIDTH * gridcolumn, HEIGHT * gridrow, WIDTH, MARGIN))
